import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000/api' });

// Attach JWT token to each request if available
API.interceptors.request.use((req) => {
  const profile = localStorage.getItem('profile');
  if (profile) {
    req.headers.Authorization = `Bearer ${JSON.parse(profile).token}`;
  }
  return req;
});

export const fetchComments = () => API.get('/comments');
export const postComment = (newComment) => API.post('/comments', newComment);
export const postReply = (newReply) => API.post('/replies', newReply);
export const loginUser = (credentials) => API.post('/user/login', credentials);
export const registerUser = (credentials) => API.post('/user/register', credentials);
