import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HomePage = () => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch comments when component mounts
    const fetchComments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/comments');
        setComments(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching comments:', err.message);
        setError('Failed to load comments.');
        setLoading(false);
      }
    };

    fetchComments();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const token = localStorage.getItem('token'); // Assuming JWT token is stored in localStorage
      const response = await axios.post(
        'http://localhost:5000/api/comments',
        { text: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      setComments([...comments, response.data]);
      setNewComment('');
    } catch (err) {
      console.error('Error posting comment:', err.message);
      setError('Failed to post comment.');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Home Page - Comments</h1>
      
      {loading ? (
        <p>Loading comments...</p>
      ) : (
        <>
          {error && <p className="text-red-500">{error}</p>}
          <ul className="mb-4">
            {comments.map((comment) => (
              <li key={comment._id} className="mb-2 p-4 border border-gray-200 rounded-lg">
                <p>{comment.text}</p>
                <small className="text-gray-500">By: {comment.author}</small>
              </li>
            ))}
          </ul>

          <form onSubmit={handleSubmit} className="bg-white shadow-lg rounded p-4">
            <h2 className="text-xl font-bold mb-4">Post a Comment</h2>
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              rows="4"
              className="w-full p-2 border border-gray-300 rounded mb-4"
              placeholder="Write your comment here..."
              required
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Post Comment
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default HomePage;
