// frontend/src/components/Comments.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Comments = () => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  const fetchComments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/comments');
      setComments(response.data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/comments', { text: newComment });
      setNewComment('');
      fetchComments(); // Refresh comments
    } catch (error) {
      console.error('Error posting comment:', error);
    }
  };

  useEffect(() => {
    fetchComments();
  }, []);

  return (
    <div>
      <h1 className="text-xl">Comments</h1>
      <form onSubmit={handleCommentSubmit} className="space-y-4">
        <textarea value={newComment} onChange={(e) => setNewComment(e.target.value)} className="textarea" placeholder="Write a comment..."></textarea>
        <button type="submit" className="button">Post Comment</button>
      </form>
      <ul>
        {comments.map((comment) => (
          <li key={comment._id} className="comment">
            {comment.text}
            {/* Add reply functionality here */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Comments;
