import React from 'react';
import ReplyForm from './ReplyForm';

function CommentList({ comments }) {
  return (
    <div className="space-y-4">
      {comments.map((comment) => (
        <div key={comment._id} className="bg-white p-4 rounded-lg shadow-md">
          <p className="text-lg">{comment.text}</p>
          <p className="text-gray-500 text-sm">by {comment.user.username}</p>
          <ReplyForm commentId={comment._id} />
          {comment.replies.map((reply) => (
            <div key={reply._id} className="ml-4 mt-2 p-2 bg-gray-100 rounded-lg">
              <p>{reply.text}</p>
              <p className="text-gray-500 text-sm">by {reply.user.username}</p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default CommentList;
